function change(a,b)
{
	if(a==1)
	{
		document.getElementById(b).style.color='#0000FF';
		
	}
	else
	{
	     if(b==x)
	     {
	     	document.getElementById(b).style.color='#FF0000';
	     }
	     else
	     {
		document.getElementById(b).style.color='#000000';
	     }
	}
}

