function change(a,b)
{
	if(a==1)
	{
		document.getElementById(b).style.color='#0000FF';
		
	}
	else
	{
	     if(b==x)
	     {
	     	document.getElementById(b).style.color='#FFFF00';
	     }
	     else
	     {
		document.getElementById(b).style.color='#FFFFFF';
	     }
	}
}

function dirty()
{
	resume=1;
}

function submitform01()
{
	
	var frmelement=document.getElementById("staff_profile_report_v0");	
	if (isMandatoryDataExists(frmelement)=="1") {    		
	
		if(resume==1)
		{      if (document.forms[1].upload_resumefiles.value==""){
				document.forms[0].upload_resume.value=document.forms[1].upload_resume.value 
			}	
			else{
			document.forms[0].upload_resume.value=document.forms[1].upload_resume.value + "|"+ document.forms[1].upload_resumefiles.value; 
			}

		       document.forms[1].submit();docHtmlSave();
		}
		else
		{
			document.forms[0].upload_resume.value=document.forms[1].upload_resumefiles.value
			docHtmlSave();
		}
	}	
}

function showchildform(formname,documentId,entity_type)
{
var entity_id=document.forms[0].entityId.value;
var document_Id=documentId;
setEditmark(documentId,formname);
window.open("./jsp/showdetailedform.jsp?entityId="+entity_id+"&documentId="+document_Id+"&formname="+formname+"&entitytype="+entity_type ,"DetailForm","width=1500,height=650,screenX=50,left=50,screenY=50,top=50,status=yes,menubar=no,scrollbars=yes,toolbar=no,modal=yes");
}

function deleteRow(i,tabname){     
     
     var delRow=document.getElementById(tabname).rows[i].cells;                    
     var cont= delRow[0].innerHTML
     var rowID="TR_"+cont;
     cont=cont.replace("#deleted","");
     delRow[0].innerHTML=cont+"#deleted";// delRow[0].innerHTML+"#deleted";          
     //document.getElementById(tabname).deleteRow(i)          
     document.getElementById(rowID).style.visibility="collapse";     
     generateIds(tabname);      
}
