function copyAddress()
{
if(document.forms[0].copy_permanent_add.checked==1){
	document.forms[0].comm_Address1.value=document.forms[0].pmt_Address1.value;
	document.forms[0].comm_Address2.value=document.forms[0].pmt_Address2.value;
	document.forms[0].comm_City.value=document.forms[0].pmt_City.value;
	document.forms[0].comm_District.value=document.forms[0].pmt_District.value;
	document.forms[0].comm_state.value=document.forms[0].pmt_state.value;
	document.forms[0].comm_country.value=document.forms[0].pmt_country.value;
	document.forms[0].comm_pincode.value=document.forms[0].pmt_pincode.value;
	document.forms[0].comm_phone.value=document.forms[0].pmt_phone.value;
}
else{
	document.forms[0].comm_Address1.value="";
	document.forms[0].comm_Address2.value="";
	document.forms[0].comm_City.value="";
	document.forms[0].comm_District.value="";
	document.forms[0].comm_state.value="";
	document.forms[0].comm_country.value="";
	document.forms[0].comm_pincode.value="";
	document.forms[0].comm_phone.value="";

}
}


