function deleteRow(i,tabname){     
     var delVal = confirm("Do you want to delete the selected record?");
     if(delVal==true)
     {
     var table_name=tabname.slice(tabname.indexOf("_")+1,tabname.length);
     var recordcount=document.getElementById("recordcount_"+table_name).value;
     document.getElementById("div_recordcount_"+table_name).innerHTML=parseInt(recordcount)-1;
     document.getElementById("recordcount_"+table_name).value=parseInt(recordcount)-1;
     var delRow=document.getElementById(tabname).rows[i].cells;                         
     delRow[0].innerHTML=delRow[0].innerHTML.replace("#Edit","");
     ajaxFunctionDelete(delRow[0].innerHTML,table_name);
     var cont= delRow[0].innerHTML;     
     var rowID="TR_"+cont;     
     cont=cont.replace("#deleted","");     
     delRow[0].innerHTML=cont+"#deleted";// delRow[0].innerHTML+"#deleted";               
     //document.getElementById(tabname).deleteRow(i)          
     generateIds(tabname);  
     document.getElementById(rowID).className="hidetd";          
     document.getElementById(rowID).style.visibility="collapse";  // not work in i.e             
         
     }
}



