function uploadFile(n){
        n=n.substring(14);
        var frmelement0=document.getElementById('staff_profile_report_v0_1');
       	if(resume==1 && document.myForm01.upload_resume.value!=""){
       	     document.getElementById('upload_status_'+n).style.color='black';	
	     document.getElementById('upload_status_'+n).innerHTML='uploading...';
	     resume=0;
	     document.myForm01.upload_resumefiles.value=document.myForm01.upload_resume.value +"|"+ document.myForm01.upload_resumefiles.value;
	     document.myForm01.submit();
       	}else if(photo==1 && document.myForm02.upload_photo.value!=""  && validateForm(frmelement0)){
	     document.getElementById('upload_status_'+n).style.color='black';	
	     document.getElementById('upload_status_'+n).innerHTML='uploading...';
	     photo=0;
	     document.myForm02.upload_photofiles.value=document.myForm02.upload_photo.value;;
	     document.myForm02.submit();
	}else{
	    document.getElementById('upload_status_'+n).style.color='red';
	    document.getElementById('upload_status_'+n).innerHTML = 'Select a file to upload';	
	}
}

