function horizontalTab(value)
{
	document.getElementById("save_staff_profile_report").className="hidetd";
	if(value=="tab1")
	{
		document.getElementById("dtab2").style.display='none';
		document.getElementById("dtab3").style.display='none';
		document.getElementById("fu1").style.display='none';
		document.getElementById("dtab5").style.display='none';
