		document.getElementById("tab1").className="horizontalTabliselected";
		document.getElementById("tab2").className="horizontalTabli";
		document.getElementById("tab3").className="horizontalTabli";
		document.getElementById("tab4").className="horizontalTabli";
		document.getElementById("tab5").className="horizontalTabli";

	}else if(value=="tab2")
	{
		document.getElementById("dtab1").style.display='none';
		document.getElementById("dtab3").style.display='none';
		document.getElementById("fu1").style.display='none';
		document.getElementById("dtab5").style.display='none';
		document.getElementById("dtab2").style.display='block';
		document.getElementById("tab2").className="horizontalTabliselected";
		document.getElementById("tab1").className="horizontalTabli";
		document.getElementById("tab3").className="horizontalTabli";
		document.getElementById("tab4").className="horizontalTabli";
		document.getElementById("tab5").className="horizontalTabli";
	}else if(value=="tab3")
	{	
		document.getElementById("save_staff_profile_report").className="listdiv_toggle";
		document.getElementById("dtab1").style.display='none';
		document.getElementById("dtab2").style.display='none';
		document.getElementById("fu1").style.display='none';
		document.getElementById("dtab5").style.display='none';
		document.getElementById("dtab3").style.display='block';
		document.getElementById("tab3").className="horizontalTabliselected";
		document.getElementById("tab1").className="horizontalTabli";
		document.getElementById("tab2").className="horizontalTabli";
		document.getElementById("tab4").className="horizontalTabli";
		document.getElementById("tab5").className="horizontalTabli";
	}else if(value=="tab4")
