function showPublications(table_id){
	if(table_id=="e_publications"){
		document.getElementById('tab_e_publications').style.display='block';
		document.getElementById('tab_journal_papers').style.display='none';
		document.getElementById('e_publications').className="horizontalTabliselected";
		document.getElementById('journal_papers').className="horizontalTabli";		
	}else{
		document.getElementById('tab_e_publications').style.display='none';
		document.getElementById('tab_journal_papers').style.display='block';	
		document.getElementById('e_publications').className="horizontalTabli";
		document.getElementById('journal_papers').className="horizontalTabliselected";
	}

}

