function save()
{	
	if(submitform_staff_profile_report_v0())
	{
		docHtmlSave();
	}
	
}


function approve()
{	
	if(submitform_staff_profile_report_v0())
	{
		docHtmlSaveAndApprove();
	}
	
}

function change(a,b)
{
	if(a==1)
	{
		document.getElementById(b).style.color='#FFFFFF;'//'#0000FF';
		
	}
	else
	{
	     if(b==x)
	     {
	     	document.getElementById(b).style.color='#FFFF00';
	     }
	     else
	     {
		document.getElementById(b).style.color='#174664';//'#FFFFFF';
	     }
	}
}

function upload_resume_dirty()
{
	resume=1;
}
function upload_photo_dirty()
{
	photo=1;
}

function showchildform(formname,documentId,entity_type)
{
var entity_id=document.forms[0].entityId.value;
var document_Id=documentId;
setEditmark(documentId,formname);
window.open("./jsp/showdetailedform.jsp?entityId="+entity_id+"&documentId="+document_Id+"&formname="+formname+"&entitytype="+entity_type ,"DetailForm","height=700, width=800, screenX=50,left=200,screenY=50,top=50,scrollbars=yes,directories=no,location=no,menubar=no,resizable=no,status=yes,toolbar=no,modal=yes");
}

function deleteRow(i,tabname){     
     
     var delRow=document.getElementById(tabname).rows[i].cells;                    
     var cont= delRow[0].innerHTML
     var rowID="TR_"+cont;
     cont=cont.replace("#deleted","");
     delRow[0].innerHTML=cont+"#deleted";// delRow[0].innerHTML+"#deleted";          
     //document.getElementById(tabname).deleteRow(i)          
     document.getElementById(rowID).style.visibility="collapse";     
     generateIds(tabname);      
}

function courses_taught()
{ 	//alert(document.forms[0].academic_term.value);
	document.getElementById("if01").src="./courses_taught?entity_id="+document.forms[0].entityId.value+"&academic_term="+document.forms[0].academic_term.value;
}
