package in.ac.dei.mhrd.omr;


import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import in.ac.dei.mhrd.omr.ManageForm;
import in.ac.dei.mhrd.omr.img.*;


import java.sql.*;

/** 
 * MyEclipse Struts
 * Creation date: 11-25-2010
 * 
 * XDoclet definition:
 * @struts.action path="/manage" name="manageForm" input="/form/manage.jsp" scope="request" validate="true"
 */
public class ManageAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ManageForm manageForm = (ManageForm) form;// TODO Auto-generated method stub
			
			HttpSession session = request.getSession(true);
			Connection con=null;
			String testName=null;
			ResultSet rs=null;
			PreparedStatement ps=null;
		
			int sec;		
			//variable for storing section
			
			int testId;
			testName=manageForm.getTestName(); 

			try{
				con=Connect.prepareConnection();
				con.setAutoCommit(false);
				ps=con.prepareStatement("select * from testheader where Test_name=?");
				ps.setString(1,testName);
				rs=ps.executeQuery();
				rs.next();
				sec=rs.getInt("Total_section");
				testId=rs.getInt("TestId");
				session.setAttribute("sec", sec);
				session.setAttribute("testName", testName);
				session.setAttribute("testId", testId);
				manageForm.setTestNo(rs.getInt("TestNo"));
				manageForm.setTestName(testName);
				manageForm.setTestId(rs.getInt("TestId"));
				manageForm.setTotalQuestion(rs.getInt("Total_question"));
				manageForm.setTotalSection(rs.getInt("Total_section"));
				manageForm.setDate(rs.getDate("Conduct_date"));
				con.commit();
				
			}catch(Exception e)
			{
				System.out.print("Exception in Manage Action"+e.getMessage());
			}
		return mapping.findForward("manage");
	}
}