package changePassword;



import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;

import dataBaseConnection.MyDataSource;
import java.sql.*;
/** 
 * MyEclipse Struts
 * Creation date: 01-01-2010
 * 
 * XDoclet definition:
 * @struts.action path="/changepasswordaction" name="passwordform" input="/WEB-INF/JspFiles/changepassword/changepassword.jsp" scope="request" validate="true"
 * @struts.action-forward name="success" path="page.changepasswordsuccess"
 */
public class RePasswordAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		PasswordForm passwordform = (PasswordForm) form;// TODO Auto-generated method stub
		HttpSession session=request.getSession();
		Connection con=null;
		request.setAttribute("passinfo","Your password has not been changed.");
		try{
			con=MyDataSource.getConnection();
			PreparedStatement ps=con.prepareStatement("update login set password=SHA1(?) where user_id=? and password=SHA1(?)");
			ps.setString(1, passwordform.getRepassword().toLowerCase());
			ps.setString(2, (String)session.getAttribute("uid"));
			ps.setString(3, passwordform.getOldpassword());
			int x=ps.executeUpdate();
			if(x>0)
				request.setAttribute("passinfo","Your password has been changed successfully!");
		}catch(Exception e){System.out.println("Exception is coming "+e);}
		finally
		{
			MyDataSource.freeConnection(con);
		}
		return mapping.findForward("passinfo");
	
	}
}