package in.ac.dei.edrp.pms.bugzillaConfig;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionErrors;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import org.apache.struts.action.ActionMessage;

/** 
 * MyEclipse Struts
 * Creation date: 30-Nov-2010
 * 
 * XDoclet definition:
 * @struts.action path="/addComponentIntoBugzilla" 
 */
public class AddComponentIntoBugAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		ComponentConfigFormIntoBugzilla projectConfigFormIntoBugzilla = (ComponentConfigFormIntoBugzilla)form;
		HttpSession session=request.getSession();
		String projectName = projectConfigFormIntoBugzilla.getProjectName();
		String componentName = projectConfigFormIntoBugzilla.getComponent();
		String[] data = new String[]{"All Open","All Closed"};
		System.out.println(projectName);
		System.out.println(componentName);
		int product_id=AddProjectInBugzilla.getDesiredId("id","products","name",projectName);
		int initialowner = AddProjectInBugzilla.getDesiredId("userid","profiles","login_name",projectConfigFormIntoBugzilla.getAssignedTo());
		int creator = AddProjectInBugzilla.getDesiredId("userid","profiles","login_name",(String)session.getAttribute("uid"));
		ActionErrors errors = new ActionErrors();
		ActionMessage error = null;
		if(AddComponentInBugzilla.insertComponents(componentName, product_id,initialowner,
				projectConfigFormIntoBugzilla.getComponentDescription())>0){
			
			AddProjectInBugzilla.insertSeries_Categories(componentName);
			AddComponentInBugzilla.insertSeries(creator, projectName, componentName, data);
			error = new ActionMessage("msg.component.added.bugzilla");
			errors.add("component_into_bugzilla_msg",error);
			saveErrors(request,errors);
		}
		else{
			error = new ActionMessage("msg.component.notadded.bugzilla");
			errors.add("component_into_bugzilla_msg",error);
			saveErrors(request,errors);
		}
		return mapping.findForward("accountCreationPage");
	}
}