package in.ac.dei.edrp.pms.updation;
/*
 * Generated by MyEclipse Struts
 * Template path: templates/java/JavaClass.vtl
 */
import in.ac.dei.edrp.pms.dataBaseConnection.MyDataSource;
import in.ac.dei.edrp.pms.viewer.checkRecord;

import javax.servlet.http.HttpServletRequest;
import javax.servlet.http.HttpServletResponse;
import javax.servlet.http.HttpSession;

import org.apache.struts.action.Action;
import org.apache.struts.action.ActionForm;
import org.apache.struts.action.ActionForward;
import org.apache.struts.action.ActionMapping;
import java.sql.*;


/** 
 * MyEclipse Struts
 * Creation date: 06-17-2009
 * 
 * XDoclet definition:
 * @struts.action path="/go3" name="editform" input="edit.jsp" scope="request" validate="true"
 */
public class EditAction extends Action {
	/*
	 * Generated Methods
	 */

	/** 
	 * Method execute
	 * @param mapping
	 * @param form
	 * @param request
	 * @param response
	 * @return ActionForward
	 */
	public ActionForward execute(ActionMapping mapping, ActionForm form,
			HttpServletRequest request, HttpServletResponse response) {
		EditForm editform = (EditForm) form;// TODO Auto-generated method stub
		
		HttpSession session=request.getSession();
		String forwardmsg="projectupdationfail";
		Connection con=null;
		
		try{
			request.setAttribute("message","Project updation failed, because this project name is already exist in the same organization.!!");
			con=MyDataSource.getConnection();//This method Established the connection from the database MySql
			if(!editform.getPname().trim().equalsIgnoreCase(editform.getOldprojectname().trim()))
			{
				if(checkRecord.twoFieldDuplicacyChecker("project_code","project","Project_Name",editform.getPname().trim(),"Valid_Org_Inportal",(String)session.getAttribute("validOrgInPortal"))!=null)
				{
					return mapping.findForward("projectupdationfail");
				}
			}
			
		/*
		 * update the 'project' table with the desired values.
		 * */
		PreparedStatement ps=con.prepareStatement("update project set Actual_End_Date=?," +
				"Target_Budget=?,Priority=?,Status=?,GChart_Color=?,Description=?" +
				" where Project_code=?");
		
		if(!editform.getActualEndDate().equals(""))
			ps.setString(1,editform.getActualEndDate());
		else
			ps.setString(1,null);
		ps.setInt(2,Integer.parseInt(editform.getTbudget()));//converting String into Integer
		ps.setString(3,editform.getPriority());
		ps.setString(4,editform.getStatus());
		ps.setString(5,editform.getGcolor());
		ps.setString(6,editform.getDarea().trim());
		ps.setString(7,(String)session.getAttribute("projectkey"));
		int n=ps.executeUpdate();
		if(n>0) /*if n is greater than zero it means update operation is successful.*/
		{
			response.sendRedirect((String)session.getAttribute("myquery"));
		}
		
		}
		catch(Exception e)
		{
			//System.out.println("error in editing project action file="+e);
		}
		finally
		{
			MyDataSource.freeConnection(con);
		}
		return mapping.findForward(forwardmsg);
		
	}
	
}